import React, { useState } from "react";
import { render } from "react-dom";

export default function Square(props) {
    const [buttonState, setButtonState] = useState(props.value);

    if(buttonState == true) {
        return (
            <button 
                className="true-square"
                value={true}
                onClick={() => setButtonState(false)}
            ></button>
        );
    } else {
        return (
            <button 
                className="false-square"
                value={false}
                onClick={() => setButtonState(true)}
            ></button>
        );
    }
}
