    const sequences = gameRows.map(row=>{
        let sequences = [];
        row.map(square=>{
            if(square.value === false) {
                sequences.push(0);
            }
            if(square.value === true) {
                sequences.push(1);
            }
        })
        return sequences;
    });

    const rowCount = sequences.map(seq =>{        
        let countSequences = [];
        let count = 0;

        seq.forEach(num=>{
            if(num === 0) {
                countSequences.push(count);
                count = 0;
            }
            if(num === 1) {
                count++
            }
        });
        countSequences.push(count);
        return countSequences;
    });

    const rowNums = rowCount.map(seq=> {
        const only0s = [];
        const onlyZero = (num) => num === 0;

        if(seq.every(onlyZero)) {
            seq.map(num=>{
                return only0s.push(num);
            });
        } 

        const cleanNums = seq.filter(num => num !== 0);

        const obj = [];
        cleanNums.map(seq=>{
            return obj.push({nums:seq});
        })
        if(only0s) {
            return obj.push({nums:0});
        }

        console.log(obj);

        return obj;
    });

    console.log(rowNums);

    return rowNums;
}
