import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';

export default function Tutorial() {
    const row1 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:1},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:2},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:3},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:4},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:5},
    ];
    const row2 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:2, col:1},
