    //         })    
    //     }

    //     const oneTotals = only1s.join();
    //     const greaterTotal = greaterThan1.length;

    //     const rawNums = `${oneTotals} ${greaterTotal}`;
    //     const nums = rawNums.replace('0', '');

    //     const obj = gameRows.map((row, index)=>{
    //         return {nums:nums, row:row[index].row, col:row[index].col };
    //     })

    //     return obj;
    // });

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        )
    });

    const board = <Board gameRows={gameRows} rowNums={rowNums} />

    return (
        <div>
            {console.log(gameCols)}
            {board}
        </div>
    );
}
