import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    const [gameRows, setGameRows] = useState(props.gameRows);

    const rowContent = {value:false, id:"square-"+nanoid(), key:"square-"+nanoid()};
    const rows = new Array(props.length).fill(rowContent);
    const column = new Array(props.height).fill(rows);

    const board = column.map(row => {
        return (
            <Row 
                row={row}
                setGameRows={setGameRows}
                key={'row-'+nanoid()}
            />
        );
    });

    // const winState = (rows, board) => {
    //     rows.map(gameRow=>{
    //         board.map(boardRow => {
    //             if(boardRow.state === gameRow.state) {
    //                 return console.log("success!");
    //             }
    //         });
    //         return;
    //     });
    // }

    return (
        <div className="board">
            {board}
        </div>
    );
}
