import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    const [gameRows, setGameRows] = useState(props.gameRows);

    const rowContent = {value:false};
    const rawRow = new Array(props.length).fill(rowContent);
    const rows = rawRow.map(row =>{
        return Object.assign({id:"square-"+nanoid()}, row)    
    });

    const [boardRows, setBoardRows] = useState(new Array(props.height).fill(rows));

    const board = boardRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
                winState={winState}
                gameRows={gameRows}
                boardRows={boardRows}
            />
        );
    });

    function winState() {
        // const a = gameRows;
        // const b = boardRows;
        const isArray = Array.isArray(gameRows) && Array.isArray(boardRows) 
        const length = gameRows.length === boardRows.length;
        const matchVals = gameRows.every((value, index) => value === boardRows[index]);

        return console.log(`is array: ${isArray}. length: ${length}. match vals: ${matchVals}`)
    }

    return (   
        <div className="board">
            {winState()}
            {board}
        </div>
    );
}
