import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';

export default function Tutorial() {
    const row1 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:1},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:2},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:3},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:4},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:1, col:5},
    ];
    const row2 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:2, col:1},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:2, col:2},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:2, col:3},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:2, col:4},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:2, col:5},
    ];
    const row3 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:3, col:1},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:3, col:2},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:3, col:3},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:3, col:4},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:3, col:5},
    ];
    const row4 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:4, col:1},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:4, col:2},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:4, col:3},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:4, col:4},
        {value:false, id:"square-"+nanoid(), key:nanoid(), row:4, col:5},
    ];
    const row5 = [
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:5, col:1},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:5, col:2},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:5, col:3},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:5, col:4},
        {value:true, id:"square-"+nanoid(), key:nanoid(), row:5, col:5},
    ];

    const gameRows = [row1, row2, row3, row4, row5];    


    // ASSIGN COL ROWS
    const nums = gameRows.map((row, index) => row[index].col);

    const gameCols = [];
    nums.forEach(num=>{
        const col = [];
        gameRows.map(row=>{
            const colNum = row.map(square=>{
                if(square.col === num) {
                    return col.push(square);
                }
            });
            return colNum;
        })
        return gameCols.push(col);
    });

    // GET NUMBERS FOR COLS
    const colCount = gameCols.map(col=>{
        let continueColSequence = false;
        let colCount = 0;
        let colSequences = [];
        // let trueDone = [];
        // let falseDone = [];
        let done = [];
        col.map(square=>{
            if(done.includes(square.id)) {
                return console.log("there you are you bleeder");
            } else {
                if(square.value === false && continueColSequence === true) {
                    continueColSequence = false;
                    colSequences.push(colCount);
                    colCount = 0;
                    console.log("value: "+ square.value + " id: " + square.id + " count: " + colCount);
                }
                if(square.value === true) {
                    continueColSequence = true;
                    colCount++
                    console.log("value: "+ square.value + " id: " + square.id + " count: " + colCount);
                }
                done.push(square.id);
                return colSequences.push(colCount);
            }
        });
        return colSequences;
    })

    // MAP COL NUMBERS TO OBJECTS
    const colNums = colCount.map(seq=> {
        let only1s = [];
        let greaterThan1 = [];

        const onlyOne = (num) => num === 1 || num === 0;

        if(seq.every(onlyOne)) {
            seq.map(num =>{
                if(num === 1) {
                    return only1s.push(num);
                }
            })
        } else {
            seq.map(num => {
                return greaterThan1.push(num);
            })    
        }

        const oneTotals = only1s.join();
        const greaterTotal = greaterThan1.length;

        const rawNums = `${oneTotals} ${greaterTotal}`;
        const nums = rawNums.replace('0', '');

        const obj = gameCols.map((col, index)=>{
            return {nums:nums, col:col[index].row, col:col[index].col };
        })

        return obj;
    });

    // GET NUMBERS FOR ROWS
    const rowCount = gameRows.map(row=>{
        let continueSequence = false;
        let count = 0;
        let sequences = []
        row.map(square=>{
            if(square.value === false && continueSequence === true) {
                continueSequence = false;
                sequences.push(count);
                count = 0;
            }
            if(square.value === true) {
                continueSequence = true;
                count++
            }
            return sequences.push(count);
        })
        return sequences;
    })

    // MAP ROW NUMBERS TO OBJECTS
    const rowNums = rowCount.map(seq=> {
        let only1s = [];
        let greaterThan1 = [];

        const onlyOne = (num) => num === 1 || num === 0;

        if(seq.every(onlyOne)) {
            seq.map(num =>{
                if(num === 1) {
                    return only1s.push(num);
                }
            })
        } else {
            seq.map(num => {
                return greaterThan1.push(num);
            })    
        }

        const oneTotals = only1s.join();
        const greaterTotal = greaterThan1.length;

        const rawNums = `${oneTotals} ${greaterTotal}`;
        const nums = rawNums.replace('0', '');

        const obj = gameRows.map((row, index)=>{
            return {nums:nums, row:row[index].row, col:row[index].col };
        })

        return obj;
    });

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        )
    });

    const board = <Board gameRows={gameRows} rowNums={rowNums} colNums={colNums} />

    return (
        <div>
            {console.log(gameCols)}
            {console.log(colCount)}
            {board}
        </div>
    );
}
