import React, { useState } from "react";

export default function Square(props) {
    const [squareValue, setSquareValue] = useState(false);
    if(props.squareValue == true) {
        return (
            <button 
                className="true-square"
                squareValue={true}
                onClick={() => setSquareValue(false)}
            ></button>
        );
    } else {
        return (
            <button 
                className="false-square"
                squareValue={false}
                onClick={() => setSquareValue(true)}
            ></button>
        );
    }
}
