export default function Rows(gameRows) {
    // GET NUMBERS FOR ROWS
    // const rowCount = gameRows.map(row=>{
    //     let count = 0;
    //     let sequences = [];
    //     let continueSeq = true;
    //     row.map(square=>{
    //         if(square.value === false && continueSeq === true) {
    //             continueSeq = false;
    //             sequences.push(count);
    //             count = 0;
    //             console.log(`FALSE -- id:${square.id}, row:${square.row}, col:${square.col}`)
    //         }
    //         if(square.value === true) {
    //             continueSeq = true;
    //             count++
    //             console.log(`TRUE -- id:${square.id}, row:${square.row}, col:${square.col}`)
    //         }
    //         return sequences.push(count);
    //     })
    //     console.log(sequences);
    //     return sequences;
    // })

    const rowCount = gameRows.map(row=>{
        let sequences = [];
        row.map(square=>{
            if(square.value === false) {
                sequences.push(0);
            }
            if(square.value === true) {
                sequences.push(1);
            }
        });

        sequences.map((num, index)=>{
            // let nextNum = index+1;
            let next = sequences[index+1];
            console.log(`current: ${num}, next: ${next}`);
        })
        return sequences;
    });

    console.log(rowCount);

    // MAP ROW NUMBERS TO OBJECTS
    const rowNums = rowCount.map(seq=> {
        let only1s = [];
        let greaterThan1 = [];

        const onlyOne = (num) => num === 1 || num === 0;

        if(seq.every(onlyOne)) {
            seq.map(num =>{
                if(num === 1) {
                    return only1s.push(num);
                }
            })
        } else {
            seq.map(num => {
                return greaterThan1.push(num);
            })    
        }

        const oneTotals = only1s.join();
        const greaterTotal = greaterThan1.length;

        const rawNums = `${oneTotals} ${greaterTotal}`;
        const nums = rawNums.replace('0', '');

        const obj = gameRows.map((row, index)=>{
            return {nums:nums, row:row[index].row, col:row[index].col };
        })

        return obj;
    });

    return rowNums;
}
