import { useState } from "react";
import FalseSquare from "./FalseSquare";
import TrueSquare from "./TrueSquare";

export default function Row(props) {
    // const [rows, setRows] = useState(props.row);

    const handleClick = (id) => {
        const updatedRow = rows.map(square => {
            if(square.id === id) {
                return {...square, value: !square.value}
            }
            return square;
        });
        setRows(updatedRow);
        // props.updateRow(updatedRow, props.row.id)
        // props.winState();
    }

    const row = props.row.map(square => {
        if(square.value === true) {
            return <TrueSquare
                value={square.value}
                id={square.id}
                handleClick={handleClick}
                key={nanoid()} />
        } else {
            return <FalseSquare 
                value={square.value}
                id={square.id}
                handleClick={handleClick}
                key={nanoid()}
            />
        }
    });

    return (
        <div className="board-row">
            {row}
        </div>
    );
}
