import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    const [gameRows, setGameRows] = useState(props.gameRows);

    const rowContent = {value:false};
    const rawRow = new Array(props.length).fill(rowContent);
    const rows = rawRow.map(row =>{
        return Object.assign({id:"square-"+nanoid()}, row)    
    });
    const boardRows = new Array(props.height).fill(rows);

    const board = boardRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
            />
        );
    });

    const winState = () => {
        gameRows.map(gameRow=>{
            boardRows.map(boardRow => {
                if(gameRow.value === boardRow.value) {
                    return console.log("success!")
                }
            });
        });
    }

    return (
        <div className="board">
            {board}
        </div>
    );
}
