import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';
import Columns from '../Helpers/ColumnHelper';
import Rows from '../Helpers/RowHelper';

export default function Tutorial() {
    const rawRows = [
        [true, true, true, true, true],
        [true, false, false, false, false],
        [true, false, false, false, false],
        [true, false, false, false, false],
        [true, true, true, true, true],
    ];

    const gameRows = rawRows.map(row=>{
        const gRow = [];
        for(let i = 1; i <= rawRows.length; i++) {
            row.map(square=>{
                if(square=== true) {
                    gRow.push({value:true, id:"square-"+nanoid(), row:i});
                } else {
                    gRow.push({value:false, id:"square-"+nanoid(), row:i});
                }
            })
        }
        return gRow;
    });

    console.log(gameRows);

    const colNums = Columns(gameRows);

    const rowNums = Rows(gameRows);

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        );
    });

    const board = <Board gameRows={gameRows} rowNums={rowNums} colNums={colNums} />

    return (
        <div>
            {board}
        </div>
    );
}
