import { nanoid } from "nanoid";
import { useState } from "react";
import FalseSquare from "./FalseSquare";
import Square from "./Square";
import TrueSquare from "./TrueSquare";

export default function Row(props) {
    const gameRows = props.rows;
    // const [gameRows, setGameRows] = useState(props.gameRows);
    // const row = rawRow.map(square => {
    //     return (
    //         <Square 
    //             value={square}
    //             key={'square'+nanoid()}
    //         />
    //     );
    // });

    const row = gameRows.map(square => {
        if(square == true) {
            return <TrueSquare value={square} key={"square-"+nanoid()} />
        } else {
            return <FalseSquare value={square} key={"square-"+nanoid()} />
        }
    })

    return (
        <div className="board-row">
            {
                gameRows ? 
                row : null
            }
        </div>
    );
}
