import { nanoid } from "nanoid";
import { useState } from "react";
import FalseSquare from "./FalseSquare";
import Square from "./Square";
import TrueSquare from "./TrueSquare";

export default function Row(props) {
    const [rows, setRows] = useState(props.row);

    const row = rows.map(square => {
        if(square == true) {
            return (
                <button 
                    className="true-square"
                    value={props.value}
                    key={"square-"+nanoid()}
                    onClick={() => console.log('true!')}
                ></button>
            );
        } else {
            return (
                <button 
                    className="false-square"
                    value={props.value}
                    key={"square-"+nanoid()}
                    onClick={() => console.log('false!')}
                ></button>
            );
        }
    })

    return (
        <div className="board-row">
            {row}
        </div>
    );
}
