import React from 'react';
import { nanoid } from 'nanoid';
import Square from '../Square';

export default function Level1(props) {
    const row1 = [false, false, false, false, false, false, false, false, false, false];
    const row2 = [false, false, false, true, false, false, false, false, false, false];
    const row3 = [false, false, true, false, false, false, true, false, false, false];

    const boardRow1 = row1.map( tf =>{
            if(tf == true) {
                Square.trueSquare();
            } else {
                Square.falseSquare();
            }
        }
    )

    const boardRow2 = row2.map(tf => {
        return (
            <Square 
                squareValue={tf}
                key={'square-'+nanoid()}
            />
        );
    })

    const boardRow3 = row3.map(tf => {
        return (
            <Square 
                squareValue={tf}
                key={'square'+nanoid()}
            />
        );
    })

    const rows = [row1, row2, row3];

    const level1 = rows.map(row => {
        row.map(tf=>{
            return (
                <Square value={tf} />
            ); 
        });
    })

    return (
        <div>
            <div className='board-row'>
                {boardRow1}
            </div>
            <div className='board-row'>
                {boardRow2}
            </div>
            <div className='board-row'>
                {boardRow3}
            </div>

            {level1}
        </div>
    );
}
