import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';

export default function Tutorial() {
    const row1 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row1Nums= {nums:"5", row:1};
    const col1Nums = {nums:"5", col:1};
    const row2 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
    ];
    const row2Nums = {nums:"1", row:2};
    const col2Nums = {nums:"1, 1", col:2};
    const row3 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row3Nums = {nums: "1", row:3};
    const col3Nums = {nums:"1, 1", col:3};
    const row4 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row4Nums = {nums:"1", row:4};
    const col4Nums = {nums:"1, 1", col:4};
    const row5 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row5Nums = {nums:"5", row:5};
    const col5Nums = {nums:"1, 1", col:5};

    const gameRows = [row1, row2, row3, row4, row5];
    const rowNums = [row1Nums, row2Nums, row3Nums, row4Nums, row5Nums];
    const colNums = [col1Nums, col2Nums, col3Nums, col4Nums, col5Nums];

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        )
    });

    const board = <Board gameRows={gameRows}  rowNums={rowNums} colNums={colNums} />

    return (
        <div>
            {board}           
            <div className='level'>
                {tutorial}
            </div>
        </div>
    );
}
