import { nanoid } from "nanoid";

export default function RowObject (rawRows) {
    const gameRows = rawRows.map(rawRow=>{
        const row = [];

        for(let i = 1; i <= rawRows.length; i++) {
            rawRow.map(square=>{
                row.push({value:square, id:"square-"+nanoid(), key:nanoid(), row:i, col:''})
            })
        }

        return row;
    });
    console.log(gameRows);       

    return gameRows;
}
