        {value:false, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row4 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row5 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
    ];

    const gameRows = [row1, row2, row3, row4, row5];

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        )
    });

    // const tutorial = gameRows.map(row=>{
