import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';
import Game from '../Game';

export default function Tutorial() {
    const row1 = [true, true, true, true, true];
    const row2 = [true, false, false, false, false];
    const row3 = [true, false, false, false, false];
    const row4 = [true, false, false, false, false];
    const row5 = [true, true, true, true, true];

    const gameRows = [row1, row2, row3, row4, row5];

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
            />
        );
    });

    // const board = <Board length={row1.length} height={rows.length} />

    return (
        // <div>
        //     {board}

        //     <div>
        //         {tutorial}
        //     </div>
        // </div>
        <div>
            <Game 
                tutorial={tutorial}
                gameRows={gameRows}
                length={row1.length} 
                height={gameRows.length}
            />
        </div>
    );
}
