import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';

export default function Tutorial() {
    const row1 = [
        {id:"row-"+nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
    ];
    const row2 = [
        {id:"row-"+nanoid()},
        {value:true, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()},  
        {value:false, id:nanoid(), key:nanoid()}, 
    ];
    const row3 = [
        {id:"row-"+nanoid()},
        {value:true, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()},  
        {value:false, id:nanoid(), key:nanoid()},
    ];
    const row4 = [
        {id:"row-"+nanoid()},
        {value:true, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()}, 
        {value:false, id:nanoid(), key:nanoid()},  
        {value:false, id:nanoid(), key:nanoid()},
    ];
    const row5 = [
        {id:"row-"+nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
        {value:true, id:nanoid(), key:nanoid()},
    ];

    const gameRows = [row1, row2, row3, row4, row5];

    // const gameRows = rows.map(row=>{
    //     return Object.assign({id:"row-"+nanoid()}, row);
    // });

    const tutorial = gameRows.map(row => {
        if(!row.id) {
            return (
                <Row 
                    row={row}
                    id={row.id}
                    key={'row-'+nanoid()}
                />
            );
        }
    });

    const board = <Board gameRows={gameRows} length={row1.length} height={gameRows.length} />

    return (
        <div>
            {board}           
            <div className='level'>
                {tutorial}
                {console.log("game rows: ")}
                {console.log(gameRows)}
            </div>
        </div>
    );
}
