import { nanoid } from "nanoid";
import { useState } from "react";
import TrueSquare from "./Square";
import FalseSquare from "./Square";

export default function Row(props) {
    const rawRow = props.row;

    const row = rawRow.map(square => {
        if(square == true) {
            return (
                <TrueSquare 
                    value={square}
                    key={'square'+nanoid()}
                />
            );
        } else {
            return (
                <FalseSquare 
                    value={square}
                    key={'square-'+nanoid()}
                />
            );
        }
    });

    return (
        <div className="board-row">
            {row}
        </div>
    );
}
