import { nanoid } from "nanoid";

export default function RowObject (rawRows) {
    const gameRows = [];
    rawRows.map(row=>{
        for(let i = 1; i <= rawRows.length; i++) {
            row.map(square=>{
                if(square=== true) {
                    gameRows.push({value:true, id:"square-"+nanoid(), row:i});
                } else {
                    gameRows.push({value:false, id:"square-"+nanoid(), row:i});
                }
            })
        }
    });
    console.log(gameRows);
    // return gameRows;
}
