import { nanoid } from "nanoid";
import Row from "./components/Row";

export default function Board(props) {
    const row = new Array(props.length).fill(false);
    const column = new Array(props.height).fill(row);

    const board = column.map(row => {
        return (
            <Row 
                value={false}
                key={'row-'+nanoid()}
            />
        );
    });

    return (
        <div className="board">
            {board}
        </div>
    );
}
