import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';
import Columns from '../Helpers/ColumnHelper';
import Rows from '../Helpers/RowHelper';
import RowObject from '../Helpers/RowObject';

export default function Tutorial() {
    const row1 = [true, true, true, true, true];
    const row2 = [true, false, false, false, false];
    const row3 = [true, false, false, false, false];
    const row4 = [true, false, false, false, false];
    const row5 = [true, true, true, true, true];

    console.log(row1, row2, row3, row4, row5);

    const rawRows = [row1, row2, row3, row4, row5];

    const gameRows = RowObject(rawRows);

    const colNums = Columns(gameRows);

    const rowNums = Rows(gameRows);

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        );
    });

    const board = <Board gameRows={gameRows} rowNums={rowNums} colNums={colNums} />

    return (
        <div>
            {board}
        </div>
    );
}
