import React, { useState } from "react";

export default function Square(props) {
    const [value, setButtonValue] = useState(props.value);

    if(value === true) {
        return (
            <button 
                className="true-square"
                value={value}
                onClick={() => setButtonValue(false)}
            ></button>
        );
    } else {
        return (
            <button 
                className="false-square"
                value={value}
                onClick={() => setButtonValue(true)}
            ></button>
        );
    }
}
