import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    const gameRows = props.gameRows;

    const bRows = gameRows.map(row=>{
        const boardRow = row.map(square=>{
            return {square, value:false, id:"square-"+nanoid(), key:nanoid()}
        })
        return boardRow;
    });

    const [boardRows, setBoardRows] = useState(bRows);


    const handleClick = (id) => {
        const updatedRow = boardRows.map(row => {
            const updatedSquare = row.map(square=>{
                if(square.id === id) {
                    return {...square, value: !square.value}
                }
                return square;
            })
            return updatedSquare
        });
        setBoardRows(updatedRow);
    }

    const board = boardRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
                handleClick={handleClick}
            />
        );
    });

    function winState() {
        const isArray = Array.isArray(gameRows) && Array.isArray(boardRows);
        const length = gameRows.length === boardRows.length;
        const gameVals = gameRows.map(gameRow => {
            const values = gameRow.map(square=>{
                return square.value;
            });
            return values;
        })
        const boardVals = boardRows.map(boardRow => {
            const values = boardRow.map(square=>{
                return square.value;
            });
            return values;
        })

        for(let i = 0; i < gameVals.length; i++) {
            if(gameVals[i] !== boardVals[i]) {
                return false;
            } else {
                return true;
            }
        }

        if(gameVals === boardVals && isArray == true && length == true) return true;

        console.log(`is array: ${isArray}. length: ${length}.`)
        console.log(` gameVals: ${gameVals}.`)
        console.log(`boardVals: ${boardVals}`)

    }

    return (   
        <div className="board">
            {
                winState()
            }
            {board}
        </div>
    );
}
