import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    // const [gameRows, setGameRows] = useState(props.gameRows);

    // const rowContent = {value:false};
    const rawRow = new Array(props.length);
    const rows = rawRow.map(row =>{
        return row.id = "square"+nanoid(), row.value = false;
    });
    const column = new Array(props.height).fill(rows);

    const board = column.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
            />
        );
    });

    // const winState = (rows, board) => {
    //     rows.map(gameRow=>{
    //         board.map(boardRow => {
    //             if(boardRow.state === gameRow.state) {
    //                 return console.log("success!");
    //             }
    //         });
    //         return;
    //     });
    // }

    return (
        <div className="board">
            {board}
        </div>
    );
}
