import { nanoid } from "nanoid";
import { useEffect, useState } from "react";
import Row from "./Row";
import Number from "./Number";

export default function Board(props) {
    const gameRows = props.gameRows;

    const bRows = gameRows.map(row=>{
        const boardRow = row.map(square=>{
            return {square, value:false, id:"square-"+nanoid(), key:nanoid()}
        })
        return boardRow;
    });

    const [boardRows, setBoardRows] = useState(bRows);


    const handleClick = (id) => {
        const updatedRow = boardRows.map(row => {
            const updatedSquare = row.map(square=>{
                if(square.id === id) {
                    return {...square, value: !square.value}
                }
                return square;
            })
            return updatedSquare
        });
        setBoardRows(updatedRow);
    }

    const rowNums = props.rowNums.map(num => {
        const row = boardRows.map(row, index => {
            if(row[index] === num.row) {
                return <Number number={num.nums} row={num.row} key={"num-"+nanoid()} />
            }
        })
        return row;
    });
    const colNums = props.colNums.map(num => {
        return <Number number={num.nums} col={num.col} key={"num-"+nanoid()} />
    })

    const board = boardRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
                handleClick={handleClick}
                // rowNums={rowNums}
                // colNums={colNums}
            />
        );
    });

    function winState() {
        const gameVals = gameRows.map(gameRow => {
            const values = gameRow.map(square=>{
                return square.value;
            });
            return values;
        })

        const boardVals = boardRows.map(boardRow => {
            const values = boardRow.map(square=>{
                return square.value;
            });
            return values;
        })

        const isArray = Array.isArray(gameVals) && Array.isArray(boardVals);
        const length = gameVals.length === boardVals.length;
        const matchVals = JSON.stringify(boardVals) === JSON.stringify(gameVals);

        if(isArray === true && length === true && matchVals === true) {
            return alert("Woohoo, you won!");
        }
    }

    useEffect(()=>{
        winState();
    })

    return (   
        <div className="board">
            {/* <div className="row-nums">{rowNums}</div>
            <div className="col-nums">{colNums}</div> */}
            <div className="game-board">{board}</div>
        </div>
    );
}
