import { nanoid } from "nanoid";

export default function RowObject (rawRows) {
    const gameRows = rawRows.map(rawRow=>{
        const gRow = [];
        for(let i = 1; i <= rawRows.length; i++) {
            rawRow.map(row=>{
                row.map(square=>{
                    const value = JSON.stringify(square);
                    gRow.push({value:value, id:"square-"+nanoid(), key:nanoid(), row:i, col:''})
                })
            })
        }

        return gRow;
    });
    console.log(gameRows);       

    return gameRows;
}
