import { nanoid } from "nanoid";
import Row from "./Row";

export default function Grid(props) {
    const boardRows = props.boardRows;
    const chunkSize = 5;
    const rowChunk = boardRows.map((row, i)=>{
        return i % chunkSize === 0 ? boardRows.slice(i, i + chunkSize) : null;
    }).filter(row => {return row});

    const grid = rowChunk.map(row=>{
        return (
            <Row 
                row={row}
                key={"row-"+nanoid()}
            />
        );
    });

    return (
        <div className="grid">
            {grid}
        </div>
    )
}
