import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';

export default function Tutorial() {
    const row1 = [
        {value:true, id:"square-tut1", key:nanoid(), row:1, col:1},
        {value:true, id:"square-tut2", key:nanoid(), row:1, col:2},
        {value:true, id:"square-tut3", key:nanoid(), row:1, col:3},
        {value:true, id:"square-tut4", key:nanoid(), row:1, col:4},
        {value:true, id:"square-tut5", key:nanoid(), row:1, col:5},
    ];
    const row2 = [
        {value:true, id:"square-tut6", key:nanoid(), row:2, col:1},
        {value:false, id:"square-tut7", key:nanoid(), row:2, col:2},
        {value:false, id:"square-tut8", key:nanoid(), row:2, col:3},
        {value:false, id:"square-tut9", key:nanoid(), row:2, col:4},
        {value:false, id:"square-tut10", key:nanoid(), row:2, col:5},
    ];
    const row3 = [
        {value:true, id:"square-tut11", key:nanoid(), row:3, col:1},
        {value:false, id:"square-tut12", key:nanoid(), row:3, col:2},
        {value:false, id:"square-tut13", key:nanoid(), row:3, col:3},
        {value:false, id:"square-tut14", key:nanoid(), row:3, col:4},
        {value:false, id:"square-tut15", key:nanoid(), row:3, col:5},
    ];
    const row4 = [
        {value:true, id:"square-tut16", key:nanoid(), row:4, col:1},
        {value:false, id:"square-tut17", key:nanoid(), row:4, col:2},
        {value:false, id:"square-tut18", key:nanoid(), row:4, col:3},
        {value:false, id:"square-tut19", key:nanoid(), row:4, col:4},
        {value:false, id:"square-tut20", key:nanoid(), row:4, col:5},
    ];
    const row5 = [
        {value:true, id:"square-tut21", key:nanoid(), row:5, col:1},
        {value:true, id:"square-tut22", key:nanoid(), row:5, col:2},
        {value:true, id:"square-tut23", key:nanoid(), row:5, col:3},
        {value:true, id:"square-tut24", key:nanoid(), row:5, col:4},
        {value:true, id:"square-tut25", key:nanoid(), row:5, col:5},
    ];

    const gameRows = [row1, row2, row3, row4, row5];    


    // ASSIGN COL ROWS
    const nums = gameRows.map((row, index) => row[index].col);

    const gameCols = [];
    nums.forEach(num=>{
        const col = [];
        gameRows.map(row=>{
            const colNum = row.map(square=>{
                if(square.col === num) {
                    return col.push(square);
                }
            });
            return colNum;
        })
        return gameCols.push(col);
    });

    // GET NUMBERS FOR COLS
    const colCount = gameCols.map(col=>{
        let colCount = 0;
        let colSequences = [];
        col.map(square=>{
            {
                if(square.value === false) {
                    colCount = 0;
                }
                if(square.value === true) {
                    colCount++
                }
                return colSequences.push(colCount);
            }
        });
        return colSequences;
    })

    // MAP COL NUMBERS TO OBJECTS
    const colNums = colCount.map(seq=> {
        let only1s = [];
        let greaterThan1 = [];

        const onlyOne = (num) => num === 1 || num === 0;

        if(seq.every(onlyOne)) {
            seq.map(num =>{
                if(num === 1) {
                    return only1s.push(num);
                }
            })
        } else {
            seq.map(num => {
                return greaterThan1.push(num);
            })    
        }

        const oneTotals = only1s.join();
        const greaterTotal = greaterThan1.length;

        const rawNums = `${oneTotals} ${greaterTotal}`;
        const nums = rawNums.replace('0', '');

        const obj = gameCols.map((col, index)=>{
            return {nums:nums, col:col[index].row, col:col[index].col };
        })

        return obj;
    });

    // GET NUMBERS FOR ROWS
    const rowCount = gameRows.map(row=>{
        let count = 0;
        let sequences = [];
        let falseSeq = [];
        row.map(square=>{
            if(square.value === false) {
                sequences.push(count);
                count = 0;
                falseSeq.push(square);
            }
            if(square.value === true) {
                count++
            }
            return sequences.push(count);
        })
        return sequences;
    })

    // MAP ROW NUMBERS TO OBJECTS
    const rowNums = rowCount.map(seq=> {
        let only1s = [];
        let greaterThan1 = [];

        const onlyOne = (num) => num === 1 || num === 0;

        if(seq.every(onlyOne)) {
            seq.map(num =>{
                if(num === 1) {
                    return only1s.push(num);
                }
            })
        } else {
            seq.map(num => {
                return greaterThan1.push(num);
            })    
        }

        const oneTotals = only1s.join();
        const greaterTotal = greaterThan1.length;

        const rawNums = `${oneTotals} ${greaterTotal}`;
        const nums = rawNums.replace('0', '');

        const obj = gameRows.map((row, index)=>{
            return {nums:nums, row:row[index].row, col:row[index].col };
        })

        return obj;
    });

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        )
    });

    const board = <Board gameRows={gameRows} rowNums={rowNums} colNums={colNums} />

    return (
        <div>
            {board}
        </div>
    );
}
