import React, { useState } from "react";
import { render } from "react-dom";

export default function Square(props) {
    const [squareValue, setSquareValue] = useState(props.squareValue);

    if(props.value == true) {
        return (
            <button 
                className="true-square"
                value={props.value}
                onClick={() => setSquareValue(false)}
            ></button>
        );
    } else {
        return (
            <button 
                className="false-square"
                value={props.value}
                onClick={() => setSquareValue(true)}
            ></button>
        );
    }
}
