import { nanoid } from "nanoid";
import { useState } from "react";
import FalseSquare from "./FalseSquare";
import TrueSquare from "./TrueSquare";

export default function Row(props) {
    const [rows, setRows] = useState(props.row);

    const handleClick = (squareId) => {
        const updatedSquare = rows.map(square => {
            if(square.id === squareId) {
                return {...square, value: !square.value}
            }
            return square;
        });
        setRows(updatedSquare);
        props.updateRow(rows, rowId);
        // props.winState();
    }

    const row = rows.map(square => {
        if(square.value === true) {
            return <TrueSquare
                value={square.value}
                id={square.id}
                rowId={props.id}
                handleClick={handleClick}
                key={nanoid()} />
        } else {
            return <FalseSquare 
                value={square.value}
                id={square.id}
                rowId={props.id}
                handleClick={handleClick}
                key={nanoid()}
            />
        }
    });

    return (
        <div className="board-row">
            {row}
        </div>
    );
}
