import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {

    const rowContent = {value:false};
    const rawRow = new Array(props.length).fill(rowContent);
    const rows = rawRow.map(row =>{
        return Object.assign({id:"square-"+nanoid()}, row)    
    });
    const column = new Array(props.height).fill(rows);

    const board = column.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
                gameRows={props.gameRows}
            />
        );
    });

    // const winState = (gameRows, rows) => {
    //     gameRows.map(gameRow=>{
    //         rows.map(boardRow => {
    //             if(gameRow.value === boardRow.value) {
    //                 console.log("success!")
    //             }
    //         });
    //         return;
    //     });
    // }

    return (
        <div className="board">
            {board}
        </div>
    );
}
