export default function CheckRow(props, gameRow, boardRow) {
    const gameVals = gameRow.map(square=>{
        return square.value;
    })

    const boardVals = boardRow.map(square=>{
        if(square.value === "x-square") {
            return false;
        } else {
            return square.value;
        }
    })

    const isArray = Array.isArray(gameVals) && Array.isArray(boardVals);
    const length = gameVals.length === boardVals.length;
    const matchVals = JSON.stringify(boardVals) === JSON.stringify(gameVals);


    if(isArray === true && length === true && matchVals === true) {
        // return true;
        const updatedRow = boardRow.map(square=>{
            if(square.value === false) {
                return {...square, value:"x-square"}
            }
            return square;
        });
        return updatedRow;
    } else {
        return console.log("row incomplete");
    }

}
