import { nanoid } from "nanoid";
import Square from "./Square";

export default function Row(props) {
    const rows = props.row;
    const rowNums = props.rowNums;
    const colNums = props.colNums;

    rowNums.map(num=>{
        rows.map(row=>{
            if(num.row === row.indexOf()) {
                rows.unshift(num);
            }
        })
    })

    const row = rows.map(square => {
        return (
            <Square
                value={square.value}
                id={square.id}
                handleClick={props.handleClick}
                key={nanoid()} 
            />
        ); 
    });

    return (
        <div className="board-row">
            {row}
        </div>
    );
}
