import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    // const [gameRows, setGameRows] = useState(props.gameRows);

    // const rows = new Array(props.length).fill(rowContent);
    for(let i = 0; i< props.height; i++) {
        const rowContent = {value:false, id:"square"+nanoid()};
        const rows = new Array(props.height).fill(rowContent);
    }
    // const column = new Array(props.height).fill(rows);

    const board = rows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
            />
        );
    });

    // const winState = (rows, board) => {
    //     rows.map(gameRow=>{
    //         board.map(boardRow => {
    //             if(boardRow.state === gameRow.state) {
    //                 return console.log("success!");
    //             }
    //         });
    //         return;
    //     });
    // }

    return (
        <div className="board">
            {board}
        </div>
    );
}
