export default function Square(props) {

    if(props.value === true) {
        return (
            <button 
                className="true-square"
                value={props.value}
                onClick={(event) => props.handleClick(props.id)}
                onContextMenu={(event) => props.handleClick(props.id)}
                id={props.id}
            ></button>
        );
    } 
    if(props.value === false) {
        return (
            <button 
                className="false-square"
                value={props.value}
                onClick={(event) => props.handleClick(props.id)}
                onContextMenu={(event) => props.handleClick(props.id)}
                id={props.id}
            ></button>
        );
    }
    if(props.value === "x-square"){
        return (
            <button 
                className="x-square"
                value="x-square"
                onClick={props.handleClick(props.id)}
                onContextMenu={() => props.handleRightClick(props.id)}
                id={props.id}
            >X</button>
        );
    }
}
