import { nanoid } from 'nanoid';
import Row from '../Row';
import Board from '../Board';

export default function Tutorial() {
    // const blankNum = {nums:"", row:""};
    const row1 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row1Nums= {nums:"5", row:0};
    const col1Nums = {nums:"5", col:0};
    const row2 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
    ];
    const row2Nums = {nums:"1", row:1};
    const col2Nums = {nums:"1, 1", col:1};
    const row3 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row3Nums = {nums: "1", row:2};
    const col3Nums = {nums:"1, 1", col:2};
    const row4 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()}, 
        {value:false, id:"square-"+nanoid(), key:nanoid()},  
        {value:false, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row4Nums = {nums:"1", row:3};
    const col4Nums = {nums:"1, 1", col:3};
    const row5 = [
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
        {value:true, id:"square-"+nanoid(), key:nanoid()},
    ];
    const row5Nums = {nums:"5", row:4};
    const col5Nums = {nums:"1, 1", col:4};

    const gameRows = [row1, row2, row3, row4, row5];    

    const rowCount = gameRows.map(row=>{
        let continueSequence = false;
        let count = 0;
        let sequences = []
        row.map(square=>{
            if(square.value === false && continueSequence === true) {
                continueSequence = false;
                sequences.push(count);
                count = 0;
            }
            if(square.value === true) {
                continueSequence = true;
                count++
            }
            return sequences.push(count);
        })
        return sequences;
    })

    const rowNums = rowCount.map(function (seq, index) {
        let only1s = [];
        let greaterThan1 = [];
        const rowNum = "row"+index;

        const onlyOne = (num) => num === 1 || num === 0;

        if(seq.every(onlyOne)) {
            seq.map(num =>{
                if(num === 1) {
                    return only1s.push(num);
                }
            })
        } 
        else {
            seq.map(num => {
                return greaterThan1.push(num);
            })    
        }

        let oneTotals = '';

        only1s.map(one=>{
            if(one != null || one != '' || one != undefined) {
                return oneTotals = only1s.join();
            }
        })

        // const oneTotals = joinOnes.filter(num => num !== '');
        const greaterTotal = greaterThan1.length;
        const nums = `${oneTotals} ${greaterTotal}`;

        const obj = {nums:nums, row:rowNum};

        return console.log(oneTotals);

        // let ones = '';
        // for(let i = 0; i< only1s.length; i++){
        //     return ones = only1s[i] + ",";
        // }
        // let greater = '';
        // for(let i = 0; i < greaterThan1.length; i++) {
        //     return greater= greaterThan1[i]+greaterThan1[i];
        // }
        // return console.log(`1s: ${ones}, greater: ${greater}`)
        // return {nums:`${nums}`, row:nanoid()}

    });

    const blankNum = {nums:"", row:""};
    // const rowNums = [blankNum, row1Nums, row2Nums, row3Nums, row4Nums, row5Nums];
    const colNums = [col1Nums, col2Nums, col3Nums, col4Nums, col5Nums];

    const tutorial = gameRows.map(row => {
        return (
            <Row 
                row={row}
                id={row.id}
                key={'row-'+nanoid()}
            />
        )
    });

    const board = <Board gameRows={gameRows} rowNums={rowNums} colNums={colNums} />

    return (
        <div>
            {/* {console.log("row count:")} */}
            {/* {console.log(rowCount)} */}
            {/* {console.log("row nums: ")} */}
            {/* {console.log(rowNums)} */}
            {board}           
            <div className='level'>
                {/* {tutorial} */}
            </div>
        </div>
    );
}
