import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    const [gameRows, setGameRows] = useState(props.gameRows);

    const rowContent = {value:false};
    const rawRow = new Array(props.length).fill(rowContent);
    const rows = rawRow.map(row =>{
        return Object.assign({id:"square-"+nanoid()}, row)    
    });
    const columns = new Array(props.height).fill(rows)

    const [boardRows, setBoardRows] = useState(columns);

    const handleClick = (id) => {
        const updatedRow = boardRows.map(square => {
            if(square.id === id) {
                return {...square, value: !square.value}
            }
            return square;
        });
        setBoardRows(updatedRow);
        props.winState();
    }

    const board = boardRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
                handleClick={handleClick}
                winState={winState}
                setBoardRows={setBoardRows}
                gameRows={gameRows}
                boardRows={boardRows}
            />
        );
    });

    function winState() {
        // const a = gameRows;
        // const b = boardRows;
        const isArray = Array.isArray(gameRows) && Array.isArray(boardRows);
        const length = gameRows.length === boardRows.length;
        const gameVals = gameRows.map(gameRow => {
            gameRow.map(square=>{
                return square.value;
            });
        })
        const boardVals = boardRows.map(boardRow => {
            boardRow.map(square=>{
                return square.value;
            });
        })
        // const matchVals = gameRows.every((gameRows.value, index) => value === boardRows[index]);


        return console.log(`is array: ${isArray}. length: ${length}. game values: ${gameVals}. board values: ${boardVals}`)
    }

    return (   
        <div className="board">
            {winState()}
            {board}
        </div>
    );
}
