import { nanoid } from "nanoid";

export default function RowObject (rawRows) {
    const gameRows = [];
    rawRows.map(rawRow=>{
        const row = [];
        rawRow.map(square=>{
            if(square=== true) {
                row.push({value:true, id:"square-"+nanoid()});
            } else {
                row.push({value:false, id:"square-"+nanoid()});
            }
        })
        for(let i = 0; i <= row.length; i++) {
            row.map(square=>{
                return row.push({row:i})
            })
        }
        gameRows.push(row);
    });

    console.log(gameRows);
    // return gameRows;
}
