import { nanoid } from "nanoid";
import { useState } from "react";
import Row from "./Row";

export default function Board(props) {
    const [gameRows, setGameRows] = useState(props.gameRows);

    const rowContent = {value:false};
    const rawRow = new Array(props.length).fill(rowContent);
    const rows = rawRow.map(row =>{
        return Object.assign({id:"square-"+nanoid()}, row)    
    });

    const [boardRows, setBoardRows] = useState(new Array(props.height).fill(rows));

    const board = boardRows.map(row => {
        return (
            <Row 
                row={row}
                key={'row-'+nanoid()}
                winState={winState}
                gameRows={gameRows}
                boardRows={boardRows}
            />
        );
    });

    function winState(a, b) {
        // const a = gameRows;
        // const b = boardRows;
        gameRows.map(gameRow=>{
            boardRows.map(boardRow=>{
                const win = Array.isArray(a) && Array.isArray(b) && a.length === b.length && a.every((val, index) => val === b[index]);
                return win;
            })
        })
        if(win === true) {
            return console.log("success!")
        }
    }

    return (   
        <div className="board">
            {winState()}
            {board}
        </div>
    );
}
