import { nanoid } from "nanoid";
import { useState } from "react";
// import FalseSquare from "./FalseSquare";
// import TrueSquare from "./TrueSquare";
import Square from "./Square";

export default function Row(props) {
    const [rows, setRows] = useState(props.row);
    const rowId = props.id;

    const handleClick = (squareId, rowId) => {
        const updatedSquare = rows.map(square => {
            if(square.id === squareId) {
                return {...square, value: !square.value}
            }
            return square;
        });
        setRows(updatedSquare);
        props.updateRow(rowId);
        // props.winState();
    }

    const row = rows.map(square => {
        return (
            <Square
                value={square.value}
                id={square.id}
                rowId={rowId}
                handleClick={handleClick}
                key={nanoid()} 
            />);

    });

    return (
        <div className="board-row">
            {row}
        </div>
    );
}
